import static ps.ns.eatapp.utils.ConstantApp.FROM_WHERE;

public class HomeFragment extends Fragment implements HomeAdapter.ListItemClickListener, View.OnClickListener {


    private HomeAdapter adapter;
    private ArrayList<HomeModel> list;
    private View view;
    private FragmentHomeBinding binding;

    public View onCreateView(@NonNull LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        TextView titleBar = getActivity().findViewById(R.id.title_bar);
        titleBar.setText("Home");
        binding = FragmentHomeBinding.inflate(getLayoutInflater());
        view = binding.getRoot();
        initViews();
        listenerViews();
        return view;

    }

    @Override
    public void onResume() {
        super.onResume();
        AppSharedMethod.statusBarShow(getActivity());
    }

    private void initViews() {
        list = new ArrayList<>();
        getDataHome();
    }

    private void listenerViews() {
        binding.icFilter.setOnClickListener(this);
    }

    private void getDataHome() {
        list.add(new HomeModel("https://images.unsplash.com/photo-1555396273-367ea4eb4db5?ixlib=rb-1.2.1&auto=format&fit=crop&w=667&q=80", "Mountain Standard,", "Open", "193 Gore Creek Dr, Vail, CO 81657, USA", 3.5f, 3.5f));
        list.add(new HomeModel("https://media-cdn.tripadvisor.com/media/photo-s/11/4a/54/fe/essence-restaurant.jpg", "Mountain Standard,", "Close", "193 Gore Creek Dr, Vail, CO 81657, USA", 3.5f, 3.5f));
        list.add(new HomeModel("https://www.samm-honfleur.com/gallery/20180315171008(1).jpg", "Mountain Standard,", "Open", "193 Gore Creek Dr, Vail, CO 81657, USA", 3.5f, 3.5f));
        list.add(new HomeModel("https://www.wien.info/media/images/41993-das-loft-sofitel-so-vienna-3to2.jpeg/image_teaser-all", "Mountain Standard,", "Close", "193 Gore Creek Dr, Vail, CO 81657, USA", 3.5f, 3.5f));
        list.add(new HomeModel("data:image/jpeg;base64,", "Mountain Standard,", "Open", "193 Gore Creek Dr, Vail, CO 81657, USA", 3.5f, 3.5f));
        list.add(new HomeModel("https://restaurantengine.com/wp-content/uploads/2015/05/startup-restaurants-typically-overspend.jpg", "Mountain Standard,", "Close", "193 Gore Creek Dr, Vail, CO 81657, USA", 3.5f, 3.5f));
        list.add(new HomeModel("https://media-cdn.tripadvisor.com/media/photo-s/1a/08/56/10/le-restaurant-avec-sa.jpg", "Mountain Standard,", "Open", "193 Gore Creek Dr, Vail, CO 81657, USA", 3.5f, 3.5f));

        binding.rvHome.setLayoutManager(new LinearLayoutManager(getContext()));
        adapter = new HomeAdapter(getActivity(), list, HomeFragment.this);
        binding.rvHome.setAdapter(adapter);
    }

    @Override
    public void onListItemClicked(int position, int viewId) {

        if (viewId == R.id.ll_home) {
            Intent intent = new Intent(getActivity(), ResturentDetailsActivity.class);
            startActivity(intent);
        }
    }

    @Override
    public void onClick(View v) {
        switch (v.getId()) {
            case R.id.ic_filter:
                openDialog();
            //    AppSharedMethod.statusBarHide(view);
                break;
        }
    }

    private void openDialog() {
        FilterDialogFragment dialog = new FilterDialogFragment();
      //  dialog.setStyle(DialogFragment.STYLE_NORMAL,0);
        dialog.show(getChildFragmentManager(), "FilterDialog");
    }

}